definition.script:
pipeline {
	agent any
	tools { go "go-1.16" }
	environment {
		NEXUS_IP = "nexus"
	}
	stages {
		stage('Pulling code') {
			steps {
				git "https://github.com/aleksandrhirs/word-cloud-generator.git"
			}
		}

		stage('Tests') {
			steps {
				sh 'make lint'
				sh 'make test'
			}
		}

		stage('Build and Uploud artifacts') {
			steps {
				sh '''sed -i "s/1.DEVELOPMENT/1.$BUILD_NUMBER/g" static/version
					GOOS=linux GOARCH=amd64 go build -o ./artifacts/word-cloud-generator -v
					gzip -f artifacts/word-cloud-generator
					ls -l artifacts/
				'''

				nexusArtifactUploader(
					nexusVersion: 'nexus3',
					protocol: 'http',
					nexusUrl: "${NEXUS_IP}:8081",
					groupId: 'pipeline',
					version: '1.$BUILD_NUMBER',
					repository: "wcg-repo",
					credentialsId: "nexus_creds",
					artifacts: [
						[artifactId: 'word-cloud-generator',
						classifier: '',
						file: 'artifacts/word-cloud-generator.gz',
						type: 'gz']
					]
				)
			}
		}
	}
}
